function Punto(x,y){this.CoorX=x;this.CoorY=y;}function Nodo(id,x,y){this.Id=id;this.Punto=new Punto(x,y);this.getDescription=function(){return this.Id+': ('+this.Punto.CoorX+', '+this.Punto.CoorY+')';};}function NodoDeformato(nodo,defX,defY,rot){this.Nodo=nodo;this.DeformataX=defX;this.DeformataY=defY;this.Rotazione=rot;}function NodoAzioniInterne(nodo,N,T,M){this.Nodo=nodo;this.Azione=N;this.Taglio=T;this.Momento=M;}function PuntoGrafico(x,y,u,v,N,T,M){this.Punto=new Punto(x,y);this.NodoDeformato=new NodoDeformato(null,u,v,null);this.NodoAzioniInterne=new NodoAzioniInterne(null,N,T,M);}function Materiale(id,nome,E,nu,alfa,gamma){this.Id=id;this.Nome=nome;this.ModuloYoung=E;this.ModuloPoisson=nu;this.CoeffDilatazioneTerm=alfa;this.PesoSpecifico=gamma;this.ModuloRigTrasv=function(){return this.ModuloYoung/(2*(1+this.ModuloPoisson));};}function Sezione(id,nome,A,I,h,chi){this.Id=id;this.Nome=nome;this.Area=A;this.MomentoInerzia=I;this.Altezza=h;this.FattoreTaglio=chi;}function Asta(id,nome,nodo1,nodo2,materiale,sezione){this.Id=id;this.Nome=nome;this.Nodo1=nodo1;this.Nodo2=nodo2;this.Materiale=materiale;this.Sezione=sezione;this.DeltaX=function(){return this.Nodo2.Punto.CoorX-this.Nodo1.Punto.CoorX;};this.DeltaY=function(){return this.Nodo2.Punto.CoorY-this.Nodo1.Punto.CoorY;};this.Lunghezza=function(){return Math.sqrt(Math.pow(this.DeltaX(),2)+Math.pow(this.DeltaY(),2));};this.Seno=function(){return this.DeltaY()/this.Lunghezza();};this.Coseno=function(){return this.DeltaX()/this.Lunghezza();};this.getDescription=function(){return this.Id+' ('+this.Nodo1.Id+' - '+this.Nodo2.Id+')';};this.CoeffDefTaglio=function(){return 12*this.Sezione.FattoreTaglio*(this.Materiale.ModuloYoung/this.Materiale.ModuloRigTrasv())*(this.Sezione.MomentoInerzia/this.Sezione.Area)/Math.pow(this.Lunghezza(),2);};}function AstaRisolta(asta,N1,T1,M1,N2,T2,M2){this.Asta=asta;this.Nodo1=new NodoAzioniInterne(asta.Nodo1,N1,T1,M1);this.Nodo2=new NodoAzioniInterne(asta.Nodo2,N2,T2,M2);}function Direzione(c,d){this.Id=c;this.Descrizione=d;}function Vincolo(id,nodo,direzione){this.Id=id;this.Nodo=nodo;this.Direzione=direzione;}function Asservimento(id,nodoMaster,nodoSlave,direzione){this.Id=id;this.NodoMaster=nodoMaster;this.NodoSlave=nodoSlave;this.Direzione=direzione;}function Nodale(id,nodo,direzione,intensita){this.Id=id;this.Nodo=nodo;this.Direzione=direzione;this.Intensita=intensita;}function Distribuito(id,nome,asta,orizzontale,verticaleInizio,verticaleFine){this.Id=id;this.Nome=nome;this.Asta=asta;this.Orizzontale=orizzontale;this.VerticaleInizio=verticaleInizio;this.VerticaleFine=verticaleFine;}function Termico(id,nome,asta,caricoTermicoSuperiore,caricoTermicoInferiore){this.Id=id;this.Nome=nome;this.Asta=asta;this.CaricoTermicoSuperiore=caricoTermicoSuperiore;this.CaricoTermicoInferiore=caricoTermicoInferiore;}